import { getContractList } from "@/api/contract/user/orguser";
import { listById } from "@/api/system/dept";
export default {
  name: "link-person-dialog",
  props: {
    showLinkPersonDialog: {
      type: Boolean,
      default: false
    },
    customerId: {
      type: String,
      default: ''
    }
  },
  data: function data() {
    return {
      loading: false,
      chooseLink: {},
      data: [],
      option: {
        highlightCurrentRow: true,
        addBtn: false,
        editBtn: false,
        delBtn: false,
        refreshBtn: false,
        columnBtn: false,
        header: false,
        border: true,
        menu: false,
        column: [{
          label: '联系人',
          prop: 'contactPerson'
        }, {
          label: '联系人类型',
          prop: 'contactType'
        }, {
          label: '联系方式',
          prop: 'contactPhone'
        }, {
          label: '邮箱',
          prop: 'contactEmail'
        }, {
          label: '部门',
          prop: 'contactDept'
        }, {
          label: '职务',
          prop: 'contactJob'
        }, {
          label: '客户经理',
          prop: 'custManager'
        }, {
          label: '所属机构',
          prop: 'custOrgName'
        }]
      },
      depts: [],
      custData: []
    };
  },
  computed: {
    ids: function ids() {
      var ids = [];
      this.custData.forEach(function (ele) {
        ids.push(ele.custOrg);
      });
      return ids.join(",");
    }
  },
  watch: {
    customerId: {
      handler: function handler(newVal, oldVal) {
        this.onLoad();
      },
      immediate: true
    }
  },
  methods: {
    closeLinkPersonDialog: function closeLinkPersonDialog() {
      this.$emit("cancelLinkPersonDialog");
    },
    onLoad: function onLoad() {
      var _this = this;

      this.loading = true;
      if (typeof this.customerId == "string" && this.customerId.length > 0) getContractList(this.customerId).then(function (res) {
        //获取机构数据
        _this.custData = res.data.data;
        listById(_this.ids).then(function (r) {
          _this.depts = r.data.data;

          _this.depts.forEach(function (item) {
            _this.custData.forEach(function (cust) {
              if (item.id == cust.custOrg) {
                cust.custOrgName = item.deptName;
              }
            });
          }); //赋值列表数据


          _this.data = _this.custData;
          _this.loading = false;
        }, function (error) {
          //赋值列表数据
          _this.data = _this.custData;
          _this.loading = false;
        });
      }, function (error) {
        window.console.log(error);
      });
    },
    handleRowClick: function handleRowClick(row, event, column) {
      this.chooseLink = row;
    },
    chooseLinkPerson: function chooseLinkPerson() {
      this.depts = [];
      this.$emit("chooseLinkPerson", this.chooseLink);
      this.$emit("cancelLinkPersonDialog");
    }
  }
};
