import { getPartnerOrderProfitTotal } from "@/api/partner/partner";
export default {
  data: function data() {
    return {
      form: {},
      query: {},
      loading: true,
      page: {
        pageSize: 10,
        currentPage: 1,
        total: 0
      },
      selectionList: [],
      option: {
        height: 'auto',
        calcHeight: 210,
        searchLabelWidth: 100,
        searchShow: true,
        addBtn: false,
        addRowBtn: false,
        editBtn: false,
        viewBtn: false,
        delBtn: false,
        searchBtn: true,
        searchMenuSpan: 6,
        tip: false,
        border: true,
        index: false,
        selection: false,
        refreshBtn: false,
        columnBtn: false,
        searchShowBtn: false,
        column: [{
          label: "合作商ID",
          prop: "id",
          hide: true
        }, {
          label: "起止时间",
          prop: "profitTime",
          type: 'datetimerange',
          format: "yyyy-MM-dd HH:mm:ss",
          valueFormat: "yyyy-MM-dd HH:mm:ss",
          hide: true,
          searchRange: true,
          searchSpan: 7,
          search: true,
          clearable: false,
          searchClearable: false,
          searchOrder: 2,
          searchValue: [this.getCurrentMonthFirst(), this.getCurrentMonthLast()]
        }, {
          label: "合作商编码",
          prop: "parCode",
          searchSpan: 5,
          search: true,
          showWordLimit: true,
          maxlength: 30
        }, {
          label: "合作商名称",
          search: true,
          prop: "parName",
          searchSpan: 5,
          showWordLimit: true,
          searchOrder: 1,
          maxlength: 30
        }, {
          label: "企业客户分润总额（元）",
          prop: "customerTotal"
        }, {
          label: "个人用户分润总额（元）",
          prop: "userTotal"
        }, {
          label: "分润总额（元）",
          prop: "total"
        }]
      },
      data: []
    };
  },
  computed: {
    ids: function ids() {
      var ids = [];
      this.selectionList.forEach(function (ele) {
        ids.push(ele.id);
      });
      return ids.join(",");
    }
  },
  methods: {
    searchReset: function searchReset() {
      this.onLoad(this.page);
    },
    searchChange: function searchChange(params, done) {
      this.query = params;
      this.page.currentPage = 1;
      this.onLoad(this.page, params);
      done();
    },
    selectionChange: function selectionChange(list) {
      this.selectionList = list;
    },
    selectionClear: function selectionClear() {
      this.selectionList = [];
      this.$refs.crud.toggleSelection();
    },
    currentChange: function currentChange(currentPage) {
      this.page.currentPage = currentPage;
    },
    sizeChange: function sizeChange(pageSize) {
      this.page.pageSize = pageSize;
    },
    onLoad: function onLoad(page) {
      var _this = this;

      var params = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : {};
      this.loading = true;

      if (this.query.profitTime) {
        this.query.startProfitTime = this.query.profitTime[0];
        this.query.endProfitTime = this.query.profitTime[1];
      } else {
        this.query.startProfitTime = this.getCurrentMonthFirst();
        this.query.endProfitTime = this.getCurrentMonthLast();
      }

      getPartnerOrderProfitTotal(page.currentPage, page.pageSize, Object.assign(params, this.query)).then(function (res) {
        var data = res.data.data;
        _this.page.total = data.total;
        _this.data = data.records;
        _this.loading = false;

        _this.selectionClear();
      });
    },
    viewOrderprofit: function viewOrderprofit(row) {
      this.$router.push({
        path: '/partner/orderprofit',
        query: {
          id: row.id,
          startProfitTime: this.query.profitTime ? this.query.profitTime[0] : this.getCurrentMonthFirst(),
          endProfitTime: this.query.profitTime ? this.query.profitTime[1] : this.getCurrentMonthLast()
        }
      });
    },
    getCurrentMonthFirst: function getCurrentMonthFirst() {
      var date = new Date();
      date.setDate(1);
      var month = parseInt(date.getMonth() + 1);
      var day = date.getDate();

      if (month < 10) {
        month = '0' + month;
      }

      if (day < 10) {
        day = '0' + day;
      }

      return date.getFullYear() + '-' + month + '-' + day + " 00:00:00";
    },
    getCurrentMonthLast: function getCurrentMonthLast() {
      var date = new Date();
      var currentMonth = date.getMonth();
      var nextMonth = ++currentMonth;
      var nextMonthFirstDay = new Date(date.getFullYear(), nextMonth, 1);
      var oneDay = 1000 * 60 * 60 * 24;
      var lastTime = new Date(nextMonthFirstDay - oneDay);
      var month = parseInt(lastTime.getMonth() + 1);
      var day = lastTime.getDate();

      if (month < 10) {
        month = '0' + month;
      }

      if (day < 10) {
        day = '0' + day;
      }

      return date.getFullYear() + '-' + month + '-' + day + " 23:59:59";
    }
  }
};
