import { getList } from "@/api/contract/product/productitem";
import { getDeptTree } from "@/api/system/dept";
export default {
  props: {
    productItemBox: {
      type: Boolean,
      default: false
    },
    id: {
      type: Number,
      default: -1
    }
  },
  data: function data() {
    return {
      form: {},
      query: {},
      loading: true,
      page: {
        pageSize: 10,
        currentPage: 1,
        total: 0
      },
      selectionList: [],
      option: {
        height: 'auto',
        menu: false,
        addBtn: false,
        selectClearBtn: false,
        calcHeight: 210,
        // searchShow: true,
        searchMenuSpan: 6,
        align: 'center',
        menuAlign: 'center',
        tip: false,
        border: true,
        // selectionFixed: false,
        index: false,
        indexLabel: '序号',
        viewBtn: false,
        selection: false,
        searchLabelWidth: 100,
        refreshBtn: false,
        columnBtn: false,
        searchShowBtn: false,
        column: [{
          label: "id",
          prop: "id",
          hide: true
        }, {
          label: "产品项名称",
          prop: "propName",
          fixed: true,
          search: true
        }, {
          label: "产品项编码",
          prop: "propCode",
          fixed: true,
          search: true,
          width: 100
        }, {
          label: "所属部门",
          prop: "propDept",
          type: "select",
          search: true,
          searchFilterable: true,
          dicData: [],
          props: {
            label: "title"
          }
        }, {
          label: "创建人",
          prop: "createUserName"
        }, {
          label: "创建时间",
          prop: "createTime",
          search: false,
          type: 'datetimerange',
          span: 24,
          row: true,
          startPlaceholder: '时间日期开始范围自定义',
          endPlaceholder: '时间日期结束范围自定义'
        }]
      },
      data: []
    };
  },
  watch: {},
  computed: {},
  methods: {
    closeDialog: function closeDialog() {
      this.$refs.crud.searchReset();
      this.$emit("close", false);
    },
    searchReset: function searchReset(param) {
      this.query = {};
      this.onLoad(this.page);
    },
    searchById: function searchById(paramId) {
      this.query = {};
      this.id = paramId;
      this.onLoad(this.page);
    },
    searchChange: function searchChange(params, done) {
      this.query = params;
      this.page.currentPage = 1;
      this.onLoad(this.page, params);
      done();
    },
    selectionChange: function selectionChange(list) {
      this.selectionList = list;
    },
    selectionClear: function selectionClear() {
      this.selectionList = [];
      this.$refs.crud.toggleSelection();
    },
    currentChange: function currentChange(currentPage) {
      this.page.currentPage = currentPage;
    },
    sizeChange: function sizeChange(pageSize) {
      this.page.pageSize = pageSize;
    },
    onLoad: function onLoad(page) {
      var _this = this;

      var params = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : {};
      this.loading = true;
      params.cateId = this.id;
      getList(page.currentPage, page.pageSize, Object.assign(params, this.query)).then(function (res) {
        var data = res.data.data;
        _this.page.total = data.total;
        _this.data = data.records;
        _this.loading = false;

        _this.selectionClear();
      });
      getDeptTree(this.form.tenantId).then(function (res) {
        var column = _this.findObject(_this.option.column, "propDept");

        column.dicData = res.data.data;
      });
    }
  }
};
