import transfer from "./transfer/index";
const PRODUCT=[{
                    label:"产品编码",
                    prop:"productNo"
                },
                {
                    label:"产品名称",
                    prop:"productName"
                },
                {
                    label:"所属部门",
                    prop:"department"
                },
                {
                    label:"产品分类",
                    prop:"productType"
                },
                {
                    label:"发布区域",
                    prop:"releaseArea"
                },
                {
                    label:"产品价格",
                    prop:"productPrice",
                    slot:true
                },
                {
                    label:"货币",
                    prop:"currency"
                }];
const PRODUCT_ITEM=[{
                        label:"产品项编码",
                        prop:"productNo"
                    },
                    {
                        label:"产品项名称",
                        prop:"productName",
                        slot:true
                    },
                    {
                        label:"所属部门",
                        prop:"department"
                    },
                    {
                        label:"产品类型",
                        prop:"productType"
                    },
                    {
                        label:"发布范围",
                        prop:"releaseArea"
                    },
                    {
                        label:"发布应用",
                        prop:"releaseApplication"
                    },
                    {
                        label:"产品价格",
                        prop:"productPrice",
                        slot:true
                    },
                    {
                        label:"货币",
                        prop:"currency"
                    }];
const COMBINATION_PRODUCT=[
                    {
                        label:"产品项名称",
                        prop:"productName"
                    },
                    {
                        label:"所属部门",
                        prop:"department"
                    },
                    {
                        label:"产品价格",
                        prop:"productPrice"
                    },
                    {
                        label:"货币",
                        prop:"currency"
                    }]; 


export default {
    name:"bind-products-dialog",
    components:{
        transfer
    },
    props:{
        showBind:{
            type:Boolean,
            default:false
        }
    },
    data(){
        return{
            productType:1,
            formPro: {},
            topList:[
                {
                    "productNo":"AD20200112001",
                    "productName":"红枣价格监测系统",
                    "department":"新华财经",
                    "productType":"客户端",
                    "releaseArea":"中国大陆",
                    "productPrice":"1000.00",
                    "releaseApplication":"专业终端",
                    "currency":"元",
                },
                {
                    "productNo":"AD20200112002",
                    "productName":"红枣价格监测系统",
                    "department":"新华财经",
                    "productType":"客户端",
                    "releaseArea":"中国大陆",
                    "productPrice":"1000.00",
                    "releaseApplication":"专业终端",
                    "currency":"元",
                }
            ],
            bottomList:[],
            topColumns:PRODUCT,
            bottomColumns:[
                {
                    label:"产品编码",
                    prop:"productNo"
                },
                {
                    label:"产品名称",
                    prop:"productName"
                },
                {
                    label:"产品部门",
                    prop:"department"
                },
                {
                    label:"产品价格",
                    prop:"productPrice"
                },
                {
                    label:"货币",
                    prop:"currency"
                },
                 {
                    label:"发布区域",
                    prop:"releaseArea"
                },
            ]
        }
    },
    methods:{
        //查询更新表格数据
        onSubmit(){},
        sureAndClose(){ 
           this.$emit("close",this.bottomList);
        },
        transferChange(rigth, left) {
            this.topList = left;
            this.bottomList = rigth;
        },
        changeRadio(val){
            if(val==1){
                this.topColumns=PRODUCT;
            }else if(val==2){
                this.topColumns=PRODUCT_ITEM;
            }else{
                this.topColumns=COMBINATION_PRODUCT;
            }
        }
    }
}
