import productitemAdd from './productItem/productitem-add';
import {getProdAddedList} from "@/api/contract/product/product";
import {getList, remove,updateItemState,exportData} from "@/api/contract/product/productitem";
import {selectItems} from "@/api/contract/product/category";
import {mapGetters} from "vuex";
import {getDeptTree} from "@/api/system/dept";
import website from '@/config/website';
import {getToken} from '@/util/auth';
export default {
  components:{
    productitemAdd,
  },
  data() {
    return {
      showAdd:false,
      id:'',
      isView:false,
      form: {},
      createTime:[],
      updateTime:[],
      query: {},
      loading: true,
      page: {
        pageSize: 10,
        currentPage: 1,
        total: 0
      },
      selectionList: [],
      option: {
        menuWidth:280,
        calcHeight: 210,
        searchMenuSpan: 6,
        tip: false,
        border: true,
        index: false,
        viewBtn: false,
        addBtn:false,
        editBtn:false,
        delBtn:false,
        menuFixed:false,
        selection: true,
        refreshBtn: false,
        columnBtn: false,
        searchShowBtn: false,
        searchLabelWidth:100,
        column: [
          {
            label: "id",
            prop: "id",
            hide: true
          },
          {
            label: "产品项名称",
            prop: "propName",
            slot:true,
            width: 240,
            searchOrder:8,
            fixed: true,
            search: true
          },
          {
            label: "产品项编码",
            prop: "propCode",
            fixed: true,
            search: true,
            searchOrder:9,
            width: 100
          },
          {
            label: "所属部门",
            prop: "propDept",
            type: "select",
            search: true,
            searchOrder:2,
            searchFilterable:true,
            dicData: [],
            props: {
              label: "title",
              value: "value"
            }
          },
          {
            label: "产品类型",
            prop: "cateId",
            search: true,
            searchOrder:6,
            type: "select",
            dicData: [],
            searchFilterable:true,
            props: {
              label: "cateName",
              value: "id"
            }
          },
          {
            label: "发布应用",
            prop: "prodId",
            search: true,
            hide:true,
            searchOrder:5,
            type: "select",
            searchFilterable:true,
            dicData: [],
            props: {
              label: "prodName",
              value: "id"
            }
          },
          {
            label: "发布范围",
            prop: "releaseScope",
            search: true,
            searchOrder:7,
            type: "select",
            searchFilterable:true,
            dicUrl: this.getDicValue(this.CONSTANT.PRODUCT_RANGE),
            props: {
              label: "dictValue",
              value: "dictKey"
            }
          },
          {
            label: "创建时间",
            prop: "createTime",
            width: 150,
            search: true,
            searchOrder:3,
            searchslot: true,
            type: 'datetimerange',
            searchSpan:12,
            row:true,
            startPlaceholder: '时间日期开始范围自定义',
            endPlaceholder: '时间日期结束范围自定义'
          },
          {
            label: "创建人",
            prop: "createUserName"
          },
          {
            label: "修改时间",
            prop: "updateTime",
            width: 150,
            search: true,
            searchOrder:1,
            searchslot: true,
            type: 'datetimerange',
            searchSpan:12,
            row:true,
            startPlaceholder: '时间日期开始范围自定义',
            endPlaceholder: '时间日期结束范围自定义'
          },
          {
            label: "更新人",
            prop: "updateUserName"
          },
          {
            label: "上架日期",
            prop: "addedDate",
            width: 150,
          },
          {
            label: "下架日期",
            prop: "expiredDate",
            width: 150,
          },
          {
            label: "产品状态",
            prop: "itemState",
            search: true,
            searchOrder:4,
            type: "select",
            dicUrl: this.getDicValue(this.CONSTANT.PRODUCT_STATE),
            props: {
              label: "dictValue",
              value: "dictKey"
            }
          }
        ]
      },
      data: []
    };
  },
   watch: {
    'form.tenantId'() {
      if (this.form.tenantId !== '') {
        getDeptTree(this.form.tenantId).then(res => {
          const column = this.findObject(this.option.column, "propDept");
          column.dicData = res.data.data;
        });
        selectItems().then(res => {
          const column = this.findObject(this.option.column, "cateId");
          column.dicData = res.data.data;
        });
        getProdAddedList().then(res => {
          const column = this.findObject(this.option.column, "prodId");
          column.dicData = res.data.data;
        });
      }
    },
  },
  computed: {
    ...mapGetters(["permission"]),
    ids() {
      let ids = [];
      this.selectionList.forEach(ele => {
        ids.push(ele.id);
      });
      return ids.join(",");
    }
  },
  methods: {
    handleExport(){
      (this.query)
      this.$confirm("是否导出产品项数据?", "提示", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      }).then(() => {
        let url = exportData();
        window.open(url+`?${website.tokenHeader}=${getToken()}`);
      });
    },
    back(){
      this.showAdd=false;
      this.onLoad(this.page);
    },
    rowItemState(ids, itemState){
      this.$confirm(itemState===1?"确定将选择数据上架?":"确定将选择数据下架?", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      }).then(() => {
          return updateItemState(ids,itemState);
        })
        .then(() => {
          this.onLoad(this.page);
          this.$message({
            type: "success",
            message: "操作成功!"
          });
        });
    },
    handleItemState(itemState){
      if (this.selectionList.length === 0) {
        this.$message.warning("请选择至少一条数据");
        return;
      }
      for(let i =0; i<this.selectionList.length;i++){
        if(this.selectionList[i].itemState === 4){
          this.$message.warning("草稿数据不能进行上下架操作!");
          return false;
        }
      }
      this.rowItemState(this.ids,itemState);

    },
    handleAdd(){
      this.showAdd=true;
      this.isView=false;
      this.id="";
      //this.$router.push({path: '/contract/product/productitemAdd'});
    },
    rowView(row) {
      console.log('view',row.id)
      this.id=row.id;
      this.isView=true;
      this.showAdd=true
    },
    rowEdit(row) {
      console.log('edit',row.id)
      this.id=row.id;
      this.isView=false;
      this.showAdd=true;
    },
    rowDel(row) {
      this.$confirm("确定将选择数据删除?", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      })
        .then(() => {
          console.log(row)
          return remove(row.id);
        })
        .then(() => {
          this.onLoad(this.page);
          this.$message({
            type: "success",
            message: "操作成功!"
          });
        });
    },
    handleDelete() {
      if (this.selectionList.length === 0) {
        this.$message.warning("请选择至少一条数据");
        return;
      }
      this.$confirm("确定将选择数据删除?", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      })
        .then(() => {
          return remove(this.ids);
        })
        .then(() => {
          this.onLoad(this.page);
          this.$message({
            type: "success",
            message: "操作成功!"
          });
          this.$refs.crud.toggleSelection();
        });
    },
    searchReset() {
      this.query = {};
      this.createTime=[]
      this.updateTime=[]
      this.onLoad(this.page);
    },
    searchChange(params, done) {
      this.query = params;
      this.page.currentPage = 1;
      if(this.createTime && this.createTime.length > 0){
        this.query.createStartTime=this.createTime[0] 
        this.query.createEndTime=this.createTime[1] 
      }
      if(this.updateTime && this.updateTime.length > 0){
        this.query.updateStartTime=this.updateTime[0] 
        this.query.updateEndTime=this.updateTime[1] 
      }
      this.onLoad(this.page, params);
      done();
    },
    selectionChange(list) {
      this.selectionList = list;
    },
    selectionClear() {
      this.selectionList = [];
      this.$refs.crud.toggleSelection();
    },
    currentChange(currentPage){
      this.page.currentPage = currentPage;
    },
    sizeChange(pageSize){
      this.page.pageSize = pageSize;
    },
    onLoad(page, params = {}) {
      this.loading = true;
      getList(page.currentPage, page.pageSize, Object.assign(params, this.query)).then(res => {
        const data = res.data.data;
        this.page.total = data.total;
        this.data = data.records;
        this.loading = false;
        this.selectionClear();
      });
      getDeptTree(this.form.tenantId).then(res => {
        const column = this.findObject(this.option.column, "propDept");
        column.dicData = res.data.data;
      });
      selectItems().then(res => {
        const column = this.findObject(this.option.column, "cateId");
        column.dicData = res.data.data;
      });
      getProdAddedList().then(res => {
        const column = this.findObject(this.option.column, "prodId");
        column.dicData = res.data.data;
      });
    }
  }
};
