import {add, getDetail, getPartnerProductList, remove, update} from "@/api/partner/partner";
import {mapGetters} from "vuex";

export default {
  data() {
    return {
      form: {},
      query: {},
      loading: true,
      partnerId: "0",
      page: {
        pageSize: 10,
        currentPage: 1,
        total: 0
      },
      selectionList: [],
      option: {
        height: 'auto',
        calcHeight: 210,
        labelWidth: 130,
        searchLabelWidth: 100,
        searchShow: true,
        addBtn: false,
        addRowBtn: false,
        editBtn: false,
        delBtn: false,
        searchBtn: true,
        menu: false,
        searchMenuSpan: 6,
        tip: false,
        border: true,
        index: false,
        viewBtn: false,
        selection: false,
        refreshBtn: false,
        columnBtn: false,
        searchShowBtn: false,
        column: [
          {
            label: "产品(项)编码",
            prop: "prodCode",
            search: true,
            showWordLimit: true,
            maxlength: 30
          },
          {
            label: "产品(项)名称",
            prop: "prodName",
            search: true,
            showWordLimit: true,
            maxlength: 30
          },
          {
            label: "所属部门",
            prop: "deptName",
            showWordLimit: true,
            maxlength: 30
          },
          {
            label: "上架时间",
            prop: "addedDate",
            showWordLimit: true,
            maxlength: 30
          },
          {
            label: "下架时间",
            prop: "expiredDate",
            showWordLimit: true,
            maxlength: 30
          },
          {
            label: "创建人",
            prop: "createUserName",
            showWordLimit: true,
            maxlength: 30
          },
          {
            label: "创建时间",
            prop: "createTime",
            showWordLimit: true,
            maxlength: 30
          },
          {
            label: "是否有效",
            prop: "isValid",
            search: true,
            type: 'select',
            props: {
              label: 'dictValue',
              value: 'dictKey'
            },
            dicUrl: this.getDicValue("is_valid")
          }
        ],
      },
      data: []
    };
  },
  computed: {
    ...mapGetters(["permission"]),
    permissionList() {
      return {
        addBtn: this.vaildData(this.permission.partner_add, false),
        viewBtn: this.vaildData(this.permission.partner_view, false),
        delBtn: this.vaildData(this.permission.partner_delete, false),
        editBtn: this.vaildData(this.permission.partner_edit, false)
      };
    },
    ids() {
      let ids = [];
      this.selectionList.forEach(ele => {
        ids.push(ele.id);
      });
      return ids.join(",");
    }
  },
  methods: {
    rowSave(row, done, loading) {
      add(row).then(() => {
        done();
        this.onLoad(this.page);
        this.$message({
          type: "success",
          message: "操作成功!"
        });
      }, error => {
        window.console.log(error);
        loading();
      });
    },
    rowUpdate(row, index, done, loading) {
      console.info(row);
      update(row).then(() => {
        done();
        this.onLoad(this.page);
        this.$message({
          type: "success",
          message: "操作成功!"
        });
      }, error => {
        window.console.log(error);
        loading();
      });
    },
    rowDel(row) {
      this.$confirm("确定将选择数据删除?", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      })
        .then(() => {
          return remove(row.id);
        })
        .then(() => {
          this.onLoad(this.page);
          this.$message({
            type: "success",
            message: "操作成功!"
          });
        });
    },
    handleDelete() {
      if (this.selectionList.length === 0) {
        this.$message.warning("请选择至少一条数据");
        return;
      }
      this.$confirm("确定将选择数据删除?", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      })
        .then(() => {
          return remove(this.ids);
        })
        .then(() => {
          this.onLoad(this.page);
          this.$message({
            type: "success",
            message: "操作成功!"
          });
          this.$refs.crud.toggleSelection();
        });
    },
    beforeOpen(done, type) {
      if (["edit", "view"].includes(type)) {
        getDetail(this.form.id).then(res => {
          this.form = res.data.data;
        });
      }
      done();
    },
    searchReset() {
      this.query = {};
      this.onLoad(this.page);
    },
    searchChange(params, done) {
      this.query = params;
      this.page.currentPage = 1;
      this.onLoad(this.page, params);
      done();
    },
    selectionChange(list) {
      this.selectionList = list;
    },
    selectionClear() {
      this.selectionList = [];
      this.$refs.crud.toggleSelection();
    },
    currentChange(currentPage) {
      this.page.currentPage = currentPage;
    },
    sizeChange(pageSize) {
      this.page.pageSize = pageSize;
    },
    onLoad(page, params = {}) {
      this.loading = true;
      this.partnerId = this.$route.query.id;
      params.partnerId = this.partnerId;
      getPartnerProductList(page.currentPage, page.pageSize, Object.assign(params, this.query)).then(res => {
        const data = res.data.data;
        this.page.total = data.total;
        this.data = data.records;
        this.loading = false;
        this.selectionClear();
      });
    }
  }
};
